jQuery(document).ready(function(){RUNCREW_STORAGE.media_frame=null;RUNCREW_STORAGE.media_link="";jQuery(".runcrew_media_selector").on("click",function(a){runcrew_show_media_manager(this);a.preventDefault();return false})});function runcrew_show_media_manager(a){RUNCREW_STORAGE.media_link=jQuery(a);if(RUNCREW_STORAGE.media_frame){RUNCREW_STORAGE.media_frame.open();return false}RUNCREW_STORAGE.media_frame=wp.media({title:RUNCREW_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:RUNCREW_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:RUNCREW_STORAGE.media_link.data("update"),close:true}});RUNCREW_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+RUNCREW_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(RUNCREW_STORAGE.media_link.data("multiple")===true){RUNCREW_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=RUNCREW_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});RUNCREW_STORAGE.media_frame.open();return false};